const button=document.getElementById("button"),count=document.getElementById("count"),popup=document.getElementById("popup");let counter=1;function clickCounter(){localStorage.clickcount?localStorage.clickcount=Number(localStorage.clickcount)+1:localStorage.clickcount=1,count.innerText=` ${localStorage.clickcount} times`}const handleClick=t=>{t.preventDefault(),popup.classList.add("popup"),clickCounter()},handleClose=t=>{t.target.closest(".alert")||popup.classList.remove("popup")};button.addEventListener("click",handleClick),popup.addEventListener("click",handleClose);