const clickBtn=document.querySelector(".section__aside-btn"),clickCounter=document.querySelector("#click"),popupInfo=document.querySelector(".popup"),popupShadow=document.querySelector(".popup__shadow"),popupCloseBtn=document.querySelector(".popup__close-btn");let countBtn=0;const showPopup=()=>{popupShadow.classList.add("active"),popupInfo.classList.add("active"),countBtn++,clickCounter.textContent=countBtn+" times"},closePopup=()=>{popupShadow.classList.remove("active"),popupInfo.classList.remove("active"),countBtn=0,clickCounter.textContent=0};clickBtn.addEventListener("click",showPopup),popupCloseBtn.addEventListener("click",closePopup);