const openPopup=document.querySelector(".btn"),closePopup=document.querySelector(".close-button"),popupBackground=document.querySelector(".black-transparent");function openPopupFunc(e){null!=e&&(e.classList.add("active"),popupBackground.classList.add("active"))}function closePopupFunc(e){null!=e&&(e.classList.remove("active"),popupBackground.classList.remove("active"))}openPopup.addEventListener("click",()=>{openPopupFunc(document.querySelector("#popup"))}),closePopup.addEventListener("click",()=>{closePopupFunc(document.querySelector("#popup"))}),popupBackground.addEventListener("click",()=>{closePopupFunc(document.querySelector("#popup.active"))});