const openPopup=document.querySelector(".btn"),closePopup=document.querySelector(".close-button"),popupBackground=document.querySelector(".black-transparent"),displayCount=document.querySelector(".strong"),resetButton=document.querySelector(".resetButton");let counter=0;function openPopupFunc(e){null!=e&&(e.classList.add("active"),popupBackground.classList.add("active"))}function closePopupFunc(e){null!=e&&(e.classList.remove("active"),popupBackground.classList.remove("active"))}function countUp(){counter++,displayCount.innerText=counter+" times",5<=counter?resetButton.classList.add("active"):resetButton.classList.remove("active")}openPopup.addEventListener("click",()=>{openPopupFunc(document.querySelector("#popup")),countUp()}),closePopup.addEventListener("click",()=>{closePopupFunc(document.querySelector("#popup"))}),popupBackground.addEventListener("click",()=>{closePopupFunc(document.querySelector("#popup.active"))}),resetButton.addEventListener("click",()=>{counter=0,displayCount.innerText="0 times"});