let popup=document.getElementsByClassName("Popup")[0],reset=document.getElementsByClassName("resetButton")[0],popupCloseButton=document.getElementsByClassName("close")[0];function showPopup(){popup.style.display="block",localStorage.clickcount?localStorage.clickcount=Number(localStorage.clickcount)+1:localStorage.clickcount=1,localStorage.clickcount<=4?document.getElementsByClassName("resetButton")[0].style.display="none":4<=localStorage.clickcount&&(document.getElementsByClassName("resetButton")[0].style.display="block"),document.getElementsByClassName("popUpClick")[0].innerHTML=`You have clicked ${localStorage.clickcount} times to related button`}reset.onclick=function(){localStorage.clickcount=0,popup.style.display="none"},popupCloseButton.onclick=function(){popup.style.display="none"},document.addEventListener("keydown",e=>{"Escape"===e.key&&(popup.style.display="none")}),window.onclick=function(e){e.target==popup&&(popup.style.display="none")};